#!/bin/shif [ $# -eq 0 ]; then echo "Usage: dwhois example.com" && exit 0; fierr_msg="is required, but it's not installed. Aborting..."command -v whois > /dev/null 2>&1 || { echo >&2 "whois $err_msg"; exit 1; }command -v sed > /dev/null 2>&1 || { echo >&2 "sed $err_msg"; exit 1; }command -v grep > /dev/null 2>&1 || { echo >&2 "grep $err_msg"; exit 1; }if grep -q ".rs" <<< "$1"; then echo "RS domains not supported. Exiting..." ; exit 0; fiserver=`whois "$1" | grep -i whois | grep -i server | head -n 1 | sed 's/.* //'`whois -Hh $server "$1"